import React, { useState } from "react";

const App = () => {
  const [currentTime, setCurrentTime] = useState("");

  const getTime = () => {
    const currTime = new Date().toLocaleTimeString();
    setCurrentTime(currTime);
  };

  return (
    <>
      <h1>{currentTime}</h1>
      <button onClick={getTime}>GET TIME</button>
    </>
  );
};

export default App;
