import React, { useState } from "react";

const Form = () => {
  // Define a state variable to store the input value
  const [inputValue, setInputValue] = useState("");

  const inputEvent = (event) => {
    // Update the inputValue state with the value from the input field
    setInputValue(event.target.value);
  };

  const handleSubmit = () => {
    // You can use inputValue here to display the name
    console.log("Submitted: " + inputValue);
  };

  return (
    <>
      <div className="Dcontainer">
        <h1 className="heading">Hello, {inputValue}</h1>
        <input
          type="text"
          style={{ height: "50px", width: "350px", marginBottom: "20px", textAlign: 'center' }}
          placeholder="Enter Your Name"
          onChange={inputEvent}
          value={inputValue} // This binds the input field value to inputValue state
        />
        <button className="primary__btn" onClick={handleSubmit}>
          Submit
        </button>
      </div>
    </>
  );
};

export default Form;
