import React, { useState } from "react";

const App = () => {
    const [count, setCount] = useState(0);

    const increment = () => {
        setCount(count + 1);
    };

    const decrement = () => {
        setCount(count - 1);
    };

    return (
        <>
            <h1>{count}</h1>
            <div>
            <button onClick={increment}>Increment</button>
            </div>
            <div></div>
            <button onClick={decrement}>Decrement</button>
        </>
    );
};

export default App;
